package com.example.abilitiesweapons.item;import com.example.abilitiesweapons.AbilitiesWeaponsMod;import com.example.abilitiesweapons.item.custom.AetheriumBladeItem;import com.example.abilitiesweapons.item.custom.VoidcallerScytheItem;import com.example.abilitiesweapons.util.ModItemTier;import net.fabricmc.fabric.api.item.v1.FabricItemSettings;import net.minecraft.item.Item;import net.minecraft.item.ItemStack;import net.minecraft.item.WrittenBookItem;import net.minecraft.nbt.NbtCompound;import net.minecraft.nbt.NbtList;import net.minecraft.nbt.NbtString;import net.minecraft.registry.Registries;import net.minecraft.registry.Registry;import net.minecraft.text.Text;import net.minecraft.util.Identifier;public class ModItems {public static final Item VOIDCALLER_SCYTHE = registerItem("voidcaller_scythe",new VoidcallerScytheItem(ModItemTier.VOIDCALLER, 9, -3.2f, new FabricItemSettings().maxCount(1)));public static final Item AETHERIUM_BLADE = registerItem("aetherium_blade",new AetheriumBladeItem(ModItemTier.AETHERIUM, 7, -2.4f, new FabricItemSettings().maxCount(1)));public static final Item ABILITIES_HANDBOOK = registerItem("abilities_handbook",new WrittenBookItem(new FabricItemSettings().maxCount(1)));private static Item registerItem(String name, Item item) {return Registry.register(Registries.ITEM, new Identifier(AbilitiesWeaponsMod.MOD_ID, name), item);}public static ItemStack createAbilitiesHandbook() {ItemStack book = new ItemStack(ABILITIES_HANDBOOK);NbtCompound nbt = book.getOrCreateNbt();nbt.putString("title", Text.translatable("item.abilitiesweapons.abilities_handbook.title").getString());nbt.putString("author", Text.translatable("item.abilitiesweapons.abilities_handbook.author").getString());nbt.putBoolean("resolved", true);NbtList pages = new NbtList();pages.add(NbtString.of(Text.translatable("item.abilitiesweapons.abilities_handbook.page1").getString()));pages.add(NbtString.of(Text.translatable("item.abilitiesweapons.abilities_handbook.page2").getString()));pages.add(NbtString.of(Text.translatable("item.abilitiesweapons.abilities_handbook.page3").getString()));pages.add(NbtString.of(Text.translatable("item.abilitiesweapons.abilities_handbook.page4").getString()));nbt.put("pages", pages);book.setNbt(nbt);return book;}public static void registerModItems() {AbilitiesWeaponsMod.LOGGER.debug("Registering Mod Items for " + AbilitiesWeaponsMod.MOD_ID);}}