package com.example.abilitiesweapons.keybinding;import com.example.abilitiesweapons.AbilitiesWeaponsMod;import com.example.abilitiesweapons.networking.ModMessages;import net.fabricmc.fabric.api.client.event.lifecycle.v1.ClientTickEvents;import net.fabricmc.fabric.api.client.keybinding.v1.KeyBindingHelper;import net.fabricmc.fabric.api.client.networking.v1.ClientPlayNetworking;import net.minecraft.client.option.KeyBinding;import net.minecraft.client.util.InputUtil;import net.minecraft.text.Text;import org.lwjgl.glfw.GLFW;public class ModKeybindings {public static final String KEY_CATEGORY_ABILITIESWEAPONS = "key.category.abilitiesweapons";public static KeyBinding activateAbilityKey;public static void register() {activateAbilityKey = KeyBindingHelper.registerKeyBinding(new KeyBinding("key.abilitiesweapons.activate_ability",InputUtil.Type.KEYSYM,GLFW.GLFW_KEY_R,KEY_CATEGORY_ABILITIESWEAPONS));registerKeyInput();}public static void registerKeyInput() {ClientTickEvents.END_CLIENT_TICK.register(client -> {if (activateAbilityKey.wasPressed()) {if (client.player != null) {ClientPlayNetworking.send(ModMessages.ABILITY_PACKET_ID, ModMessages.createAbilityPacket());}}});}}