package com.example.abilitiesweapons.networking;import com.example.abilitiesweapons.AbilitiesWeaponsMod;import com.example.abilitiesweapons.networking.packet.AbilityC2SPacket;import net.fabricmc.fabric.api.networking.v1.ServerPlayNetworking;import net.minecraft.network.PacketByteBuf;import net.minecraft.network.codec.PacketCodec;import net.minecraft.network.codec.PacketCodecs;import net.minecraft.network.packet.CustomPayload;import net.minecraft.util.Identifier;public class ModMessages {public static final Identifier ABILITY_PACKET_ID = new Identifier(AbilitiesWeaponsMod.MOD_ID, "ability_packet");public static record AbilityPacketPayload() implements CustomPayload {public static final PacketCodec<PacketByteBuf, AbilityPacketPayload> CODEC = PacketCodecs.BOOL.xmap((b) -> new AbilityPacketPayload(), (payload) -> true);public static final CustomPayload.Id<AbilityPacketPayload> ID = new CustomPayload.Id<>(ABILITY_PACKET_ID);@Overridepublic Id<? extends CustomPayload> getId() {return ID;}}public static AbilityPacketPayload createAbilityPacket() {return new AbilityPacketPayload();}public static void registerC2SPackets() {ServerPlayNetworking.registerGlobalReceiver(ABILITY_PACKET_ID, AbilityC2SPacket::receive);}}