package com.example.abilitiesweapons.util;import com.example.abilitiesweapons.AbilitiesWeaponsMod;import net.minecraft.item.ItemConvertible;import net.minecraft.item.ItemTier;import net.minecraft.recipe.Ingredient;import net.minecraft.registry.tag.ItemTags;import net.minecraft.util.Lazy;import java.util.function.Supplier;public enum ModItemTier implements ItemTier {VOIDCALLER(5, 2000, 10.0F, 8.0F, 15, () -> Ingredient.fromTag(ItemTags.IRON_INGOTS)),AETHERIUM(4, 1500, 9.0F, 6.0F, 22, () -> Ingredient.fromTag(ItemTags.GOLD_INGOTS));private final int miningLevel;private final int itemDurability;private final float miningSpeed;private final float attackDamage;private final int enchantability;private final Lazy<Ingredient> repairIngredient;ModItemTier(int miningLevel, int itemDurability, float miningSpeed, float attackDamage, int enchantability, Supplier<Ingredient> repairIngredient) {this.miningLevel = miningLevel;this.itemDurability = itemDurability;this.miningSpeed = miningSpeed;this.attackDamage = attackDamage;this.enchantability = enchantability;this.repairIngredient = new Lazy<>(repairIngredient);}@Overridepublic int getDurability() {return this.itemDurability;}@Overridepublic float getMiningSpeedMultiplier() {return this.miningSpeed;}@Overridepublic float getAttackDamage() {return this.attackDamage;}@Overridepublic int getMiningLevel() {return this.miningLevel;}@Overridepublic int getEnchantability() {return this.enchantability;}@Overridepublic Ingredient getRepairIngredient() {return this.repairIngredient.get();}}